mapper.select:
select
	*
from
	tag T join space_tag ST
	using(tag_no)
where ST.space_no = #{ spaceNo }

mapper.update:
update 
	review
set
	review_comment = #{content}
where
	review_no = #{reviewNo}

mapper.select:
select 
	category_name 
from space join category using(category_no)
where space_no=#{spaceNo}

mapper.insert:
insert into
	space_option 
values (
	#{ spaceNo },
	#{ optionNo }
)

insert into
	space_tag 
values (
	#{ spaceNo },
	#{ tagNo }
)

mapper.update:
update 
	space
set
	star_avg = #{starAvg}
where
	space_no = #{spaceNo}

mapper.select:
select space_no from ( SELECT rank()over(partition by space_no order by OPTION_NO) as rnum, E.* FROM ( select * from space left join space_option using(space_no) left join option_List using(option_no) left join category using(category_no)
option_name = #{option}
and category_name = #{category}
and address like '%'|| #{location} ||'%'
) E ) group by space_no

mapper.update:
update 
	space
set
	status = #{status}
where
	space_no = #{spaceNo}
